/*--------------Form handler----------------*/

$(document).ready(function(){


	$('[data-submit]').on('click', function(e){
		e.preventDefault();
		$(this).parents('.subscribe__form, .inner__form, .cabinet__form, .review-form, .subscr__form, .order-form, .callme-form').submit();
	})
	$.validator.addMethod(
		"regex",
		function(value, element, regexp) {
			var re = new RegExp(regexp);
			return this.optional(element) || re.test(value);
		},
			"Please check your input."
	);
	function valEl(el){

		if(!el.length || typeof el.validate !== "function") return;

		el.validate({
			rules:{
				tel:{ /*Проверка по данным атрибута name*/
					required:true,
					regex: '^([\+]+)*[0-9\x20\x28\x29\-]{11,20}$'
				},
				name:{
					required:true
				},
				email:{
					required:true,
					email:true
				},
				password:{
					required:true
				},
				learn:{
					required:true
				},
				tel1:{
					required:false,
					regex: '^([\+]+)*[0-9\x20\x28\x29\-]{11,20}$'
				},
				name1:{
					required:false
				},
				email1:{
					required:false,
					email:true
				},
				password1:{
					required:false
				}
			},
			messages:{
				tel:{
					required:'This field is required',
					regex:'The phone number may contain the symbols + - ()'
				},
				name:{
					required:'This field is required',
				},
				password:{
					required:'This field is required',
				},
				learn:{
					required:'! You must read and accept the rules',
				},
				email:{
					required:'This field is required', 
					email:'Invalid email format'
				},
				tel1:{
					required:'This field is required',
					regex:'The phone number may contain the symbols + - ()'
				},
				email1:{
					required:'This field is required', 
					email:'Invalid email format'
				}
			},

		submitHandler: function (form) {
			$('#form-loader').fadeIn();// Здесь меняем появление лоадера на необходимые действия
			var $form = $(form);
			var $formId = $(form).attr('id');

			switch($formId){

				case'popupOrder':
					$.ajax({
					type: 'POST',
					url: $form.attr('action'),
					data: $form.serialize(),
					})
				.always(function (response) {
					setTimeout(function (){
						$('#form-loader').fadeOut();
					},1000);
					setTimeout(function (){
						 $.magnificPopup.open({
							items: {
								src: '#taken'
							}
						});
						$form.trigger('reset');
						$('input').removeClass('valid');
						//строки для остлеживания целей в Я.Метрике и Google Analytics
					},1100);
					setTimeout(function (){
						$.magnificPopup.close();
					},4000);
				});
				break;

				case'displayMessage':
					$.ajax({
					type: 'POST',
					url: $form.attr('action'),
					data: $form.serialize(),
					})
				.always(function (response) {
					setTimeout(function (){
						$('#form-loader').fadeOut();
					},1000);
					setTimeout(function (){
						$('.edit>.modal-сaution').fadeIn();
						$form.trigger('reset');
						$('input').removeClass('valid');
						//строки для остлеживания целей в Я.Метрике и Google Analytics
					},1100);
					setTimeout(function (){
						$('.edit>.modal-сaution').fadeOut();
					},4000);
				});
				break;

				case'popupMessage':
					$.ajax({
					type: 'POST',
					url: $form.attr('action'),
					data: $form.serialize(),
					})
				.always(function (response) {
					setTimeout(function (){
						$('#form-loader').fadeOut();
					},1000);
					setTimeout(function (){
						 $.magnificPopup.open({
							items: {
								src: '#callback'
							}
						});
						$form.trigger('reset');
						$('input, textarea').removeClass('valid');
						//строки для остлеживания целей в Я.Метрике и Google Analytics
					},1100);
					setTimeout(function (){
						$.magnificPopup.close();
					},4000);
				});
				break;

				case'popupSubscribe':
					$.ajax({
					type: 'POST',
					url: $form.attr('action'),
					data: $form.serialize(),
					})
				.always(function (response) {
					setTimeout(function (){
						$('#form-loader').fadeOut();
					},1000);
					setTimeout(function (){
						 $.magnificPopup.open({
							items: {
								src: '#sub'
							}
						});
						$form.trigger('reset');
						$('input').removeClass('valid');
						//строки для остлеживания целей в Я.Метрике и Google Analytics
					},1100);
					setTimeout(function (){
						$.magnificPopup.close();
					},4000);
				});
				break;

			}
			return false; 
		}
	});
}

	const forms = $('form.subscribe__form, .inner__form, .cabinet__form, .review-form, .subscr__form, .order-form, .callme-form'); 
	if (forms.length) {
		forms.each(function() {
			valEl($(this));
		});
	}




});

